import DataTable from "react-data-table-component";
import { useEffect, useState } from "react";

export default function Table() {
	const [loading, setLoading] = useState(true);
	const [counList, setCounList] = useState([]);

	const columns = [

		{
			name: "Flags",
			cell: row => <img src={row?.flags?.png} width={50} alt=''></img>,
			selector: row => row.flags.png,
			width: '100px',
			sortable: true,
		},
		{
			name: "Country Name",
			selector: row => row.name,
			sortable: true,
		},
		{
			name: "2 character Country Code",
			selector: row => row.cca2,
			sortable: true,
		},
		{
			name: "3 character Country Code",
			selector: row => row.cca3,
			sortable: true,
		},
		{
			name: "Native Country Name",
			selector: row => row.name.nativeName[Object.keys(row.nativeName)[0]]?.official,
			sortable: true,
		},
		{
			name: "Alternative Country Name",
			selector: row => row.altSpellings,
			sortable: true,
		},
		{
			name: "Country Calling Codes",
			selector: row => row.idd,
			sortable: true,
		},
	];

	function getCounList() {
		fetch("https://restcountries.com/v3.1/all")
			.then(res => res.json())
			.then(counList => {
				const data = counList.map((value, index) => {
					return {
						flags: `${value.flags.png}`,
						name: `${value.name.official}`,
						cca2: `${value.cca2}`,
						cca3: `${value.cca3}`,
						nativeName: `${value.name.nativeName[Object.keys(value.nativeName)[0]]?.official}`,
						altSpellings: `${value.altSpellings}`,
						idd: `${value.idd.root}`,
					};
				});

				setCounList(data);
			});
	}

	useEffect(() => {
		getCounList();
	}, []);

	useEffect(() => {
		if (counList.length > 0) {
			console.log(counList);
			setLoading(false);
		}
	}, [counList]);
	
	const paginationComponentOptions = {
		rowsPerPageText: 'Row Per Pages',
		rangeSeparatorText: 'of',
		selectAllRowsItem: true,
	};
	
	// function MyComponent() {
	// 	return <DataTable columns={columns} data={counList} pagination paginationComponentOptions={paginationComponentOptions} />;
	// }

	return (
		<div className="table">
			<DataTable
				title="Counties List"
				striped
				bordered
				small
				columns={columns}
				data={counList}
				pagination
				paginationPerPage = {25}
				paginationComponentOptions={paginationComponentOptions}
				paginationRowsPerPageOptions={[25, 50, 100]}
				progressPending={loading}
				progressComponent={<h1>LOADING...</h1>}
				selectableRows
				highlightOnHover
			/>
		</div>
	);
}
