import DataTable from "react-data-table-component";
import { useEffect, useState } from "react";

export default function Table() {
	const [loading, setLoading] = useState(true);
	const [counList, setCounList] = useState([]);

	const columns = [

		{
			id: "flage",
			name: "Flage",
			selector: row => row.flage,
		},
		{
			id: "name",
			name: "Country Name",
			selector: row => row.name,
			sortable: true,
		},
		{
			name: "2 character Country Code",
			selector: row => row.cca2,
		},
		{
			name: "3 character Country Code",
			selector: row => row.cca3,
		},
		{
			name: "Native Country Name",
			selector: row => row.name.nativeName,
		},
		{
			name: "Alternative Country Name",
			selector: row => row.altSpellings,
		},
		{
			name: "Country Calling Codes",
			selector: row => row.idd,
		},
	];

	function getCounList() {
		fetch("https://restcountries.com/v3.1/all")
			.then(res => res.json())
			.then(counList => {
				const data = coinList.map((value, index) => {
					return {
						flage: `${value.flage}`,
						name: `${value.name.official}`,
						cca2: `${value.cca2}`,
						cca3: `${value.cca3}`,
						nativeName: `${value.name.nativeName}`,
						altSpellings: `${value.altSpellings}`,
						iid: `${value.idd}`,
					};
				});

				setCoinList(data);
			});
	}

	useEffect(() => {
		getCoinList();
	}, []);

	useEffect(() => {
		if (counList.length > 0) {
			console.log(counList);
			setLoading(false);
		}
	}, [counList]);

	return (
		<div className="table">
			<DataTable
				title="Counties List"
				striped
				bordered
				small
				columns={columns}
				data={counList}
				pagination
				progressPending={loading}
				progressComponent={<h1>LOADING...</h1>}
				selectableRows
			/>
		</div>
	);
}
